mapper.select:
SELECT

FROM permission

    deleted = 0

        AND parent_id = #{dto.parentId}


        AND permission_type = #{dto.permissionType}


        AND permission_name LIKE CONCAT('%', #{dto.permissionName}, '%')


        AND permission_code LIKE CONCAT('%', #{dto.permissionCode}, '%')


        AND status = #{dto.status}


        AND visible = #{dto.visible}


        AND create_time >= #{dto.createTimeStart}


        AND create_time <= #{dto.createTimeEnd}


ORDER BY parent_id ASC, sort_order ASC, create_time DESC
